document.addEventListener("DOMContentLoaded",()=>{const e=document.getElementById("product-selector"),t=document.getElementById("product-selector-button");if(t===null||e==null)return;t.addEventListener("click",()=>{e.style.display==="block"?(e.style.display="none",t.classList.remove("remove-bottom-radius")):(e.style.display="block",t.classList.add("remove-bottom-radius"))}),window.addEventListener("click",n=>{n.target.id.includes("product-selector")||(e.style.display="none",t.classList.remove("remove-bottom-radius"))})})