document.addEventListener("DOMContentLoaded",()=>{const e=document.getElementById("dropdown-content"),t=document.getElementById("navbar-sites-button"),n=document.getElementById("navbar-sites-button-icon");t.addEventListener("click",()=>{n.classList.toggle("rotate-chevron"),e.style.display==="block"?(e.style.display="none",t.classList.remove("remove-bottom-radius")):(e.style.display="block",t.classList.add("remove-bottom-radius"))}),window.addEventListener("click",s=>{!s.target.matches("#navbar-sites-button")&&!s.target.matches("#navbar-sites-button-icon")&&(e.style.display!=="none"&&e.style.display!==""&&n.classList.toggle("rotate-chevron"),e.style.display="none",t.classList.remove("remove-bottom-radius"))})})